# -*- coding: utf-8 -*-
import base64, struct
bmp_data = base64.b64decode('')
def bmp_info(data):
    s = struct.unpack('<ccIIIIIIHH', data[:30])
    if s[0] + s[1] == b'BM':
        return {
            'width': s[-4],
            'height': s[-3],
            'color': s[-1]
        }
    else:
        return ('Not Windows bmp file')

bi = bmp_info(bmp_data)
assert bi['width'] == 28
assert bi['height'] == 10
assert bi['color'] == 16
print('ok')